import React from 'react';


const Navigation = ({onRouteChange, isSignedIn, routeState})=>
{
     const signOutRoute = <p onClick={() => onRouteChange('signout')} className='f3 link dim black underline pa3 pointer'>Sign Out</p>;
    const signinOrRegisterRoute = [
        <p key='signin' onClick={() => onRouteChange('signin')} className='f3 link dim black underline pa3 pointer'>Sign In</p>,
        <p key ='register' onClick={() => onRouteChange('register')} className='f3 link dim black underline pa3 pointer'>Register</p>
    ] 
    let result;
    switch(routeState)
    {
        case 'signin':
         result = <p onClick={() => onRouteChange('signout')} className='f3 link dim black underline pa3 pointer'>Sign Out</p>
         break;
        case 'signout':
        result =<p onClick={() => onRouteChange('signin')} className='f3 link dim black underline pa3 pointer'>Sign In</p>
        break;
        case 'register':
        result =  <p onClick={() => onRouteChange('register')} className='f3 link dim black underline pa3 pointer'>Register</p>
        break;

    }
    const routeOptions = isSignedIn === true ? signOutRoute : signinOrRegisterRoute;
    return(
        <nav style={{display: 'flex', justifyContent: 'flex-end'}}>
        {result}
       </nav>
    );
}
export default Navigation;
