import React from 'react';


const Navigation = ({onRouteChange, isSignedIn, state})=>
{
    const signOutRoute = <p onClick={() => onRouteChange('signout')} className='f3 link dim black underline pa3 pointer'>Sign Out</p>;
    // const signinOrRegisterRoute = [
    //     <p key='signin' onClick={() => onRouteChange('signin')} className='f3 link dim black underline pa3 pointer'>Sign In</p>,
    //     <p key ='register' onClick={() => onRouteChange('register')} className='f3 link dim black underline pa3 pointer'>Register</p>
    // ]
    const signinRoute = <p key ='signin' onClick={() => onRouteChange('signin')} className='f3 link dim black underline pa3 pointer'>Sign In</p>

    const registerRoute = <p key ='register' onClick={() => onRouteChange('register')} className='f3 link dim black underline pa3 pointer'>Register</p>

    const routeOptions = isSignedIn === true ? signOutRoute : signOutRoute;

    const homeRoute = <p key ='home' onClick={() => onRouteChange('home')} className='f3 link dim black underline pa3 pointer'>Sign home</p>

    const route = state === 'register' ? true: false;

    const routeHome = state === 'home' ? true: false;
    if(isSignedIn)
    {
        return(
            <nav style={{display: 'flex', justifyContent: 'flex-end'}}>
            {signOutRoute}
           </nav>
        );
    }
    if(route)
    {
        return(
            <nav style={{display: 'flex', justifyContent: 'flex-end'}}>
            {registerRoute}
           </nav>
        );
    }
    if(routeHome)
    {
        return(
            <nav style={{display: 'flex', justifyContent: 'flex-end'}}>
            {homeRoute}
           </nav>
        );
    }
    return(
        <nav style={{display: 'flex', justifyContent: 'flex-end'}}>
        {routeOptions}
       </nav>
    );
}
export default Navigation;
